mapper.update:
update user_label
set user_id = #{userId,jdbcType=INTEGER},
  label_1 = #{label1,jdbcType=VARCHAR},
  label_2 = #{label2,jdbcType=VARCHAR},
  label_3 = #{label3,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, user_id, label_1, label_2, label_3
from user_label
where id = #{id,jdbcType=INTEGER}
