mapper.update:
update bill
set type = #{type,jdbcType=VARCHAR},
  money = #{money,jdbcType=DOUBLE},
  remark = #{remark,jdbcType=VARCHAR},
  time = #{time,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, type, money, remark, time
from bill
where id = #{id,jdbcType=INTEGER}
